class SignUpPodcastFragment : BaseFragment<FragmentSignUpPodcastBinding>() {

    private lateinit var signUpAdapter: SignUpPodcastAdapter
    private var image = "data:image/jpeg;base64,"

    override fun inflate(
        inflater: LayoutInflater,
        container: ViewGroup?
    ): FragmentSignUpPodcastBinding {
        return FragmentSignUpPodcastBinding.inflate(inflater, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        signUpAdapter = SignUpPodcastAdapter()
        binding.recyclerviewPodcast.apply {
            layoutManager = GridLayoutManager(requireContext(), 3)
            adapter = signUpAdapter
            addItemDecoration(ScrollAlphaItemDecoration(
                minAlpha = 0.1f
            ))
        }

        signUpAdapter.differ.submitList(listOf<PodcastModel>(
            PodcastModel(id = "1", image, "Billie Ellish"),
            PodcastModel(id = "2", image, "Billie Ellish"),
            PodcastModel(id = "3", image, "Billie Ellish"),
            PodcastModel(id = "4", image, "Billie Ellish"),
            PodcastModel(id = "5", image, "Billie Ellish"),
            PodcastModel(id = "6", image, "Billie Ellish"),
            PodcastModel(id = "7", image, "Billie Ellish"),
            PodcastModel(id = "8", image, "Billie Ellish"),
            PodcastModel(id = "9", image, "Billie Ellish"),
            PodcastModel(id = "10", image, "Billie Ellish"),
            PodcastModel(id = "11", image, "Billie Ellish"),
            PodcastModel(id = "12", image, "Billie Ellish"),
            PodcastModel(id = "13", image, "Billie Ellish"),
            PodcastModel(id = "14", image, "Billie Ellish"),
            PodcastModel(id = "15", image, "Billie Ellish"),
            PodcastModel(id = "16", image, "Billie Ellish"),
            PodcastModel(id = "17", image, "Billie Ellish"),
            PodcastModel(id = "18", image, "Billie Ellish"),
            PodcastModel(id = "19", image, "Billie Ellish"),
